import React, { useState } from "react";
import axios from "axios";
import TranslationResults from "./TranslationResults";

export default function Translation(props) {
  // console.log(props);
  let [translationResponse, setTranslationResponse] = useState(null);
  let languageCodes = ["de", "pl", "es"];
  let languageCode = "de";

  function handleTranslationResponse(response) {
    console.log(response);
    setTranslationResponse(response.data.exact_matches[0].translations[0].text);
  }

  function searchTranslation() {
    let translationApiUrl = `https://linguee-api.herokuapp.com/api?dst=${languageCode}&q=${props.keyword}&src=en`;

    axios.get(translationApiUrl).then(handleTranslationResponse);
  }
  searchTranslation();

  return (
    <div className="Translation">
      <h3>Check this word out in different languages:</h3>
      <ul>
        {languageCodes.map(function (value, index) {
          return (
            <div key={index}>
              <TranslationResults value={value} />
            </div>
          );
        })}
      </ul>

      <h4>{translationResponse}</h4>
    </div>
  );
}
