import React, { useState } from "react";
import axios from "axios";

export default function Translation(props) {
  // console.log(props);
  let [translationResponse, setTranslationResponse] = useState(null);

  function handleTranslationResponse(response) {
    console.log(response);
    setTranslationResponse(response.exact_matches[0].translations[0].text);
  }

  function searchTranslation(event) {
    event.preventDefault();
    let translationApiUrl = `https://linguee-api.herokuapp.com/api?dst=de&q=${props.keyword}&src=en`;

    axios.get(translationApiUrl).then(handleTranslationResponse);
  }
  return (
    <div className="Translation">
      searchTranslation()
      <h4>Hello from {props.keyword}</h4>
      <h4>Hello from {translationResponse}</h4>
    </div>
  );
}
